import pandas as pd

class Input: 
        
    def __init__(self, df_n, df_t, program, years):
        self.df_n = df_n
        self.df_t = df_t
        self.program = program
        self.years = years 

    def get_df_n(self, df):
        self.df_n = df

    def get_df_t(self, df):
        self.df_t = df

    def get_program(self, program):
        self.program = program

    def get_years(self, years):
        self.years = years

        
if __name__ == "__main__":
    d = {'col1': [1, 2], 'col2': [3, 4]}
    df = pd.DataFrame(data=d)

    input = Input(df, "Biology", 2019)
    print(input.df, input.program, input.years)
